mapper.select:
select count(*) 
  from loan_history 
 where user_id = #{user_id}

select * 
  from loan_history
 where user_id = #{user_id} 
  order by loan_date desc
  limit #{cri.skip}, #{cri.amount}

select count(*) 
  from like_history 
 where user_id = #{user_id}

select * 
  from like_history
 where user_id = #{user_id} 
  order by like_no desc
  limit #{cri.skip}, #{cri.amount}
